import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import NavBar from './NavBar';
import '../styles/userHome.css';

function UserHome() {
    const [channels, setChannels] = useState([]);
    const username = sessionStorage.getItem("username"); // 假设从 sessionStorage 获取用户名

    useEffect(() => {
        fetchChannels();
    }, []); 

    const fetchChannels = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/channels', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        const data = await response.json();
        console.log("Display Channels: ", data)
        if (data.Success) {
          console.log('Display Chennels Success:', data);
          setChannels(data.channels);
        } else {
          console.log('Display failed');
        }
      } catch (error) {
        console.error('Error during dislplay channels:', error);
      }
    }

    return (
        <div className="home-container">
            <NavBar username={username} />
            <div className="main-content">
                <div className="channel-list">
                    {channels.map(channel => (
                        <div key={channel.id} className="channel">
                            {channel.name}
                        </div>
                    ))}
                </div>
                <div className="channel-content">
                    {/* Channel content will go here */}
                    <h2>Channel Content</h2>
                </div>
            </div>
        </div>
    );
}

export default UserHome;
