import React, { useEffect, useState } from 'react';
import '../styles/replyThread.css';

function ReplyThread({ messageId }) {
  const [messageDetails, setMessageDetails] = useState(null);
  const [reply, setReply] = useState('');

  useEffect(() => {
    // 假设你有一个API从messageId获取消息的详细信息
    if (messageId) {
      fetchMessageDetails();
    }
  }, [messageId]);

  const fetchMessageDetails = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/messages/${messageId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      console.log("message data: ", data)
      if (data.Success) {
        setMessageDetails(data.message);
        console.log("messageDetails: ", messageDetails )
      } else {
        console.log('Failed to fetch message details');
      }
    } catch (error) {
      console.error('Error fetching message details:', error);
    }
  };

  const handleSendReply = async () => {
    // 发送回复的逻辑，可能需要调用API并传递reply内容
    console.log("Sending reply:", reply);
    // 清空回复框
    setReply('');
  };

  return (
    <div className="reply-thread">
      {messageDetails && (
        <>
          <div className="message-details">
            <div className="message-author">{messageDetails.author}:</div>
            <div className="message-content">{messageDetails.content}</div>
          </div>
          {/* 未来用于显示回复的组件或元素 */}
          <div className="replies-container">
            {/* 回复将在这里显示 */}
          </div>
          <div className="reply-input-container">
            <input
              type="text"
              placeholder="Type your reply..."
              value={reply}
              onChange={(e) => setReply(e.target.value)}
              className="reply-input"
            />
            <button onClick={handleSendReply} className="send-reply-button">Send</button>
          </div>
        </>
      )}
    </div>
  );
}

export default ReplyThread;
