// App.jsx

import React, { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from 'react-router-dom';
import LoginPage from './components/Login';
import UserHome from './components/UserHome';
import Profile from './components/Profile';
import CreateChannel from './components/CreateChannel';
import ChannelContent from './components/ChannelContent';

const App = () => {
  const [userLoggedIn, setUserLoggedIn] = useState(sessionStorage.getItem("user_id") != null);

  // handler for logged in or registered users
  function setLogin(userId) {
    setUserLoggedIn(true);
    sessionStorage.setItem("user_id", userId); // Set a real user id after login



  const redirectUrl = sessionStorage.getItem('redirectUrl');
    if (redirectUrl) {
      sessionStorage.removeItem('redirectUrl');
      window.location.replace(redirectUrl);
    } else {
      window.location.replace('/login'); 
    }
  }

  useEffect(() => {
    // This effect runs once on mount to check if the user is logged in
    const loggedIn = sessionStorage.getItem("user_id") != null;
    setUserLoggedIn(loggedIn);
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage onLoginClick={setLogin} />} />
        <Route path="/" element={<Navigate replace to={userLoggedIn ? "/home" : "/login"} />} />
        <Route path="/home" element={userLoggedIn ? <UserHome /> : handleProtectedRouteAccess('/home')} />
        <Route path="/profile" element={userLoggedIn ? <Profile /> : handleProtectedRouteAccess('/profile')} />
        <Route path="/createChannel" element={userLoggedIn ? <CreateChannel /> : handleProtectedRouteAccess('/createChannel')} />
        <Route path="/channel/:channelId" element={userLoggedIn ? <ChannelContent /> : handleProtectedRouteAccess(window.location.pathname)} />
        <Route path="/channel/:channelId/message/:messageId" element={userLoggedIn ? <ChannelContent /> : handleProtectedRouteAccess(window.location.pathname)} />
        <Route path="*" element={<Navigate replace to={userLoggedIn ? "/home" : "/login"} />} />
      </Routes>
    </Router>
  );
};

export default App;
