// ChannelContent.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import NavBar from './NavBar';
import ChannelList from './ChannelList';
import MessageThread from './MessageThread'; 
// import '../styles/channelContent.css';

function ChannelContent() {
    const username = sessionStorage.getItem("username");
    const userid = sessionStorage.getItem("user_id");
    const { channelId } = useParams(); 
    const [channelName, setChannelName] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchChannelDetails();
    }, [channelId]); 

    const fetchChannelDetails = async () => {
        try {
            // 注意更新URL以包含实际的channelId
            const response = await fetch(`http://127.0.0.1:5000/api/channel/${channelId}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            const data = await response.json();
            if (data.Success) {
                // 假设返回的频道详情在data.message中
                console.log("channel data: ", data)
                setChannelName(data.message.name); // 根据返回的数据结构设置频道名称
            } else {
                console.log('Channel not found');
            }
        } catch (error) {
            console.error('Error fetching channel details:', error);
        }
    };


    const postMessage = async () => {
        if (!message.trim()) {
            alert("Please enter a message.");
            return;
        }
        try {
            const response = await fetch(`http://127.0.0.1:5000/api/channels/${channelId}/messages/post`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    userid: userid, 
                    content: message,
                }),
            });
            const data = await response.json();
            if (data.success) {
                console.log('Message posted successfully');
                setMessage(''); // 清空输入框
                // 可以在这里添加逻辑来刷新消息列表，显示新消息
            } else {
                console.log('Failed to post message:', data.error);
            }
        } catch (error) {
            console.error('Error posting message:', error);
        }
    };



    return (
        <div className="home-container">
            <NavBar username={username} />
            <div className="main-content">
                <ChannelList />
                <div className="channel-content">
                    {/* 频道名称 */}
                    <div className="channel-name">
                        <h2>Channel: {channelName}</h2>
                    </div>
                    {/* 消息显示区域，已由MessageThread组件承担 */}
                    <MessageThread />
                    {/* 消息输入区域 */}
                    <div className="message-input-container">
                        <input
                            type="text"
                            placeholder="Type a message..."
                            className="message-input"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                        />
                        <button className="post-message-button" onClick={postMessage}>Send</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ChannelContent;
