import React, { useState } from 'react';
import NavBar from './NavBar';
import ChannelList from './ChannelList';
import '../styles/userHome.css';

function UserHome() {
  const username = sessionStorage.getItem("username");
  // State to manage the visibility of the message thread
  const [isMessageThreadVisible, setMessageThreadVisible] = useState(False);

  // Function to toggle the message thread visibility
  const toggleMessageThread = () => {
    setMessageThreadVisible(!isMessageThreadVisible);
  };

  return (
    <div className="home-container">
      <NavBar username={username} />
      <div className="main-content">
        <ChannelList />
        <div className="channel-content">
          {/* Placeholder for channel-specific content */}
          <h2>Channel Content: Choose a Channel</h2>
        </div>
        {isMessageThreadVisible && (
          <div className="message-thread">
            {/* Placeholder for message thread content */}
            <h3>Message Thread: Empty Message</h3>
            {/* Button to dismiss the message thread */}
            {/* <button onClick={toggleMessageThread} className="dismiss-thread-btn"> */}
              {/* Dismiss
            </button> */}
          </div>
        )}
      </div>
    </div>
  );
}

export default UserHome;
