// MessageThread.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/messageThread.css';

function MessageThread() {
  const { channelId } = useParams(); // 从URL中获取channelId参数
  const [messages, setMessages] = useState([]); // 用于存储消息列表的状态

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/channels/${channelId}/messages`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        const data = await response.json();
        if (data.Success) {
          setMessages(data.message); // 存储消息到状态
        } else {
          console.log('Failed to fetch messages');
        }
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, [channelId]); // 当channelId变化时重新获取消息

  return (
    <div className="message-thread">
      {/* 动态渲染消息 */}
      {messages.length > 0 ? (
        messages.map((message) => (
          <div key={message.id} className="message">
            <div className="message-author">{message.author}:</div>
            <div className="message-content">{message.content}</div>
          </div>
        ))
      ) : (
        <h3>No messages in this thread</h3>
      )}
    </div>
  );
}

export default MessageThread;
