// ChannelList.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ChannelList() {
  const [channels, setChannels] = useState([]);
  let navigate = useNavigate(); // 使用useNavigate钩子

  useEffect(() => {
    fetchChannels();
  }, []);

  const fetchChannels = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/channels', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      if (data.Success) {
        setChannels(data.channels);
      } else {
        console.log('Display failed');
      }
    } catch (error) {
      console.error('Error during display channels:', error);
    }
  };

  const handleChannelClick = (channelName) => {
    // 导航到/channel/:channelName
    navigate(`/channel/${channelName}`);
  };

  return (
    <div className="channel-list">
      {channels.map(channel => (
        <div key={channel.id} className="channel" onClick={() => handleChannelClick(channel.name)}>
          {channel.name}
        </div>
      ))}
    </div>
  );
}

export default ChannelList;
