// App.jsx

import React, { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from 'react-router-dom';
import LoginPage from './components/Login';
import UserHome from './components/UserHome';
import Profile from './components/Profile';
import CreateChannel from './components/CreateChannel';
import ChannelContent from './components/ChannelContent';

const App = () => {
  const [userLoggedIn, setUserLoggedIn] = useState(sessionStorage.getItem("user_id") != null);
  const [redirectPath, setRedirectPath] = useState('');

  function setLogin(userId) {
    sessionStorage.setItem("user_id", userId);
    setUserLoggedIn(true);
    // Instead of redirecting here, update state to trigger a redirect.
    const redirectUrl = sessionStorage.getItem('redirectUrl') || '/home';
    sessionStorage.removeItem('redirectUrl'); // Clean up
    setRedirectPath(redirectUrl); // Set path to redirect
  }

  useEffect(() => {
    const loggedIn = sessionStorage.getItem("user_id") != null;
    setUserLoggedIn(loggedIn);
  }, []);

  useEffect(() => {
    // This effect listens for changes to redirectPath and redirects if needed
    if (redirectPath) {
      window.location.replace(redirectPath);
    }
  }, [redirectPath]);

  return (
    <Router>
      <Routes>
        {/* Routes that do not require login */}
        <Route path="/login" element={<LoginPage onLoginClick={setLogin} />} />
        <Route path="/" element={<Navigate replace to={userLoggedIn ? "/home" : "/login"} />} />

        {/* Protected routes that require login */}
        <Route path="/home" element={userLoggedIn ? <UserHome /> : (() => {
          sessionStorage.setItem('redirectUrl', '/home');
          return <Navigate replace to="/login" />;
        })()} />
        <Route path="/profile" element={userLoggedIn ? <Profile /> : (() => {
          sessionStorage.setItem('redirectUrl', '/profile');
          return <Navigate replace to="/login" />;
        })()} />
        <Route path="/createChannel" element={userLoggedIn ? <CreateChannel /> : (() => {
          sessionStorage.setItem('redirectUrl', '/createChannel');
          return <Navigate replace to="/login" />;
        })()} />
        <Route path="/channel/:channelId" element={userLoggedIn ? <ChannelContent /> : (() => {
          sessionStorage.setItem('redirectUrl', '/home');
          return <Navigate replace to="/login" />;
        })()} />
        <Route path="/channel/:channelId/message/:messageId" element={userLoggedIn ? <ChannelContent /> : (() => {
          sessionStorage.setItem('redirectUrl', '/home');
          return <Navigate replace to="/login" />;
        })()} />

        {/* Redirect all other paths to home if logged in, or login page if not */}
        <Route path="*" element={<Navigate replace to={userLoggedIn ? "/home" : "/login"} />} />
      </Routes>
    </Router>
  );
};

export default App;
