// App.jsx
import React from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from 'react-router-dom';
import LoginPage from './components/Login';
import UserHome from './components/UserHome';
import Profile from './components/Profile';
import CreateChannel from './components/CreateChannel';
import ChannelContent from './components/ChannelContent'; 

const App = () => {
  // state for if user is logged in or not
  const [userLoggedIn, setLoggedIn] = React.useState(
    () => sessionStorage.getItem("user_id") != null
  );

  // handler for logged in or registered users
  function SetLogin() {
    setLoggedIn(true);
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/home" element={<UserHome />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/createChannel" element={<CreateChannel />} />
        <Route path="/channel/:channelId" element={<ChannelContent />} />
        <Route path="/channel/:channelId/message/:messageId" element={<ChannelContent />} />
      </Routes>
    </Router>
  );
};

export default App;
